import React, { useState } from 'react';
import './App.css';
import pokemon from 'pokemontcgsdk'

pokemon.configure({ apiKey: 'ee4d4085-9702-4e4b-abaa-d6f970c3594f' })
function App() {
  let [counter, setCounter] = useState(2);
  let handleClick = (incrementValue) => setCounter(counter + incrementValue);
  return (
    <div className="App">
      <header className="App-header">
        <div id="counter">
          <div className="grid-container">
            <Form />
            <Button handleClickFunc={handleClick} increment={8} />
            <Button handleClickFunc={handleClick} increment={4} />
            <Button handleClickFunc={handleClick} increment={2} />
            <Display message={counter} />
          </div>
        </div>
      </header>
    </div>
  );
}

function Button(props) {
  const handleClick = () => props.handleClickFunc(props.increment)
  return (<button onClick={handleClick}>
    +{props.increment}
  </button>)
}

function Display(props) {
  return (
    <div className="display">{props.message}</div>
  );
}

class Form extends React.Component {
  pokemonNameInput = React.createRef();
  pokemons = pokemon.card.where({pageSize: 105, q: "set.id:base1" })
  handleChange = async() => {
    let pokemonss = await this.pokemons;
    const result = Object.values(pokemonss)[0];
    console.log(result.find(({ name }) => name === "Alakazam"));
    //let currentMon = result.filter(name => name.includes(this.pokemonNameInput.current.value))[0];
    //console.log(currentMon);
    /*pokemonss.card.where({q: `name:${this.pokemonNameInput.current.value}*` })
      .then(result => {
        console.log(result.data[0].name) // "Blastoise"
      })*/
    //console.log(this.pokemonNameInput.current.value);
  };
  render() {
    return (
      <div>
        <input type="text" placeholder="Pokemon" onChange={this.handleChange} ref={this.pokemonNameInput} />
        <button>Search!</button>
      </div>
    );
  }
}

export default App;
