import { useState } from 'react';
import './App.css';

function App() {
  let [counter, setCounter] = useState(2);
  let handleClick = (increment) => setCounter(counter * increment);
  return (
    <div className="App">
      <header className="App-header">
        <Button handleClickFunc={handleClick} increment={4}/>
        <Button handleClickFunc={handleClick} increment={2}/>
        <Display message={counter}/>
      </header>
    </div>
  );
}

function Button(props) {

  return (<button onClick={props.handleClickFunc(props.increment)}>
    {props.increment}
  </button>)
}

function Display(props){
  return(
    <div>{props.message}</div>
  );
}

export default App;
